import { useEffect, useState } from "react";
import { Link } from "react-router-dom";

import { Element } from "../views/Inventory/element";
import { Atracciones } from "../views/Inventory/data";

import edit from "../assets/images/fichaedit.svg";
import borrar from "../assets/images/fichadelete.svg";

function Borrar({id}){
    delete Atracciones[id];
}
function Buttons({display, id}){
    if(display){
        return(
            <div class="product-buttons">
                <button class="product-btn"><img src={edit} alt="" onClick={Buttons(id)}></img></button>
                <button class="product-btn"><img src={borrar} alt="" onClick={Buttons(id)}></img></button>
            </div>
        );
    }
}
export const Producto = ({id, titulo, imgURL, action}) => {
    //const picture = (import(`${this.props.src}`));
    const [product] = useState();
    return(
        <div class="product-card">
            <div class="product-title">
                <h1>{titulo}</h1>
            </div>
            <div class="product-image">
                <img src={imgURL}/>
            </div>
            <Buttons display={action} id/>
        </div>

    );
};
