import React, { useState, useEffect } from "react";
import { ReactTitle } from "react-meta-tags";
import { Navbar } from "../../componentes/Navbar";
import { Header } from "../../componentes/Header";
import { Footer } from "../../componentes/Footer";
import { Barra } from "../../componentes/BarraMitad";
import "bootstrap/dist/css/bootstrap.css";
import { library } from '@fortawesome/fontawesome-svg-core';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrash, faPlus } from '@fortawesome/free-solid-svg-icons';
import { nanoid } from 'nanoid';
library.add(faTrash, faPlus);


export const UserMPH = () => {

    const [userName, setUserName] = useState("");

    useEffect(() => {
        const savedUserData = localStorage.getItem("userData");

        if (savedUserData) {
            const userDataObject = JSON.parse(savedUserData);
            const { nombre } = userDataObject;

            setUserName(nombre);
        }
    }, []);

    const [users, setUsers] = useState([
        {
            id: nanoid(),
            name: "Bustos, Sofía",
            role: "Community Manager",
            phone: "+57 3125378754",
            type: "empleada",
            email: "sofbus@gmail.com",
            imageUrl: "https://img.freepik.com/foto-gratis/retrato-hermoso-mujer-joven-posicion-pared-gris_231208-10760.jpg"
        },
        {
            id: nanoid(),
            name: "Marín, Angela",
            role: "Community Manager",
            phone: "+57 31255346782",
            type: "empleada",
            email: "marian@gmail.com",
            imageUrl: "https://www.mundopsicologos.com/site/article/60606/50895/las-10-cualidades-de-una-persona-mas-valoradas-0_ai1.jpg"
        }
    ]);

    return (
        <div className="loginpage">
            <ReactTitle title="Gestión de usuarios" />
            <Navbar />
            <Header />
            <Barra titulo={`Bienvenid@ admin`} />
            <div className="full-wrap">
            <div className="admin-wrapper">
                    <div className="button-section">
                        <div className="button-obj top">
                            <p>Gestión de usuarios</p>
                        </div>
                        <a href="/dashboard">
                            <div className="button-obj">
                                <p>Dashboard</p>
                            </div>
                        </a>
                        <a href="/registro-eventos">
                            <div className="button-obj">
                                <p>Registro de eventos</p>
                            </div>
                        </a>
                        <a href="/gestion-espacios">
                            <div className="button-obj">
                                <p>Gestión de espacios</p>
                            </div>
                        </a>
                        <a href="#">
                            <div className="button-obj">
                                <p>Ajustes</p>
                            </div>
                        </a>
                    </div>
                </div>

                <div className="section-wrapper">
                    {/* {users.map((user) => (
                        <div key={user.id} className="obj-wrapper">
                            <div className="user-obj">
                                <img src={user.imageUrl} alt="" />
                                <div className="info">
                                    <span id="nombre">{user.name}</span>
                                    <span id="puesto">{user.role}</span>
                                    <span id="cel">{user.phone}</span>
                                    <span id="rol">{user.type}</span>
                                    <span id="email">{user.email}</span>
                                </div>
                            </div>
                            <div className="user-obj-footer">
                                <button className="borrar" style={{ fontSize: '24px', color: 'red' }}><FontAwesomeIcon icon={faTrash} /></button>
                                <a href={`/usuarios-editar`}>
                                    <button className="editar" style={{ fontSize: '24px', color: 'green' }}><FontAwesomeIcon icon={faPlus} /></button>
                                </a>
                            </div>
                        </div>
                    ))} */}
                </div>
            </div>
            <Footer />
        </div>
    );
};
