import { useEffect, useState } from "react";
import { Link } from "react-router-dom";

import { Element } from "../views/Inventory/element";

import Atracciones from "../assets/data.json";

import edit from "../assets/images/fichaedit.svg";
import borrar from "../assets/images/fichadelete.svg";

export const Producto = ({id, titulo, imgURL, action}) => {
    const [items, setItems] = useState([]);

    useEffect(() => {
        setItems(Atracciones);
    }, []);

    const deleteItem = (id) => {

    };
    function Borrar({id}){
        const newItems = items.filter((item) => item.id !== id);
        setItems(newItems);
    }
    function Buttons({display, id}){
        if(display){
            return(
                <div class="product-buttons">
                    <button class="product-btn"><img src={edit} alt="" ></img></button>
                    <button class="product-btn"><img src={borrar} alt="" onClick={Borrar({id})}></img></button>
                </div>
            );
        }
    }
    const [product] = useState();
    return(
        <div class="product-card">
            <div class="product-title">
                <h1>{titulo}</h1>
            </div>
            <div class="product-image">
                <img src={imgURL}/>
            </div>
            <Buttons display={action} id/>
        </div>

    );
};
