import React from "react";

import edit from "../assets/images/fichaedit.svg";
import borrar from "../assets/images/fichadelete.svg";
function funca () {
    console.log('The link was clicked.');
}
function Buttons({display}){
    if(display){
        return(
            <div class="product-buttons">
                <button class="product-btn"><img src={edit} alt="" onClick={funca()}></img></button>
                <button class="product-btn"><img src={borrar} alt="" onClick={funca()}></img></button>
            </div>
        );
    }
}
export const Producto = ({titulo, imgURL, action}) => {
    //const picture = (import(`${this.props.src}`));

    return(
        <div class="product-card">
            <div class="product-title">
                <h1>{titulo}</h1>
            </div>
            <div class="product-image">
                <img src={imgURL}/>
            </div>
            <Buttons display={action}/>
        </div>

    );
};
