import React, { useState } from "react";

export const LoginForm = () => {
  // State for login form data
  const [loginData, setLoginData] = useState({
    email: "",
    password: "",
  });

  // Function to handle login form input changes
  const handleLoginChange = (e) => {
    const { id, value } = e.target;
    setLoginData({ ...loginData, [id]: value });
  };

  // Function to handle login form submission
  const handleLoginSubmit = (e) => {
    e.preventDefault();

    // Retrieve signup form data from localStorage
    const savedUserData = localStorage.getItem("userData");

    if (savedUserData) {
      // Parse the JSON string back to an object
      const userDataObject = JSON.parse(savedUserData);

      // Now you can access individual fields from the signup form data
      const { email: savedEmail, password: savedPassword } = userDataObject;

      // Use the retrieved data for login validation
      if (
        loginData.email === savedEmail &&
        loginData.password === savedPassword
      ) {
        console.log("Login successful!");
        // Redirect or perform other actions for successful login
      } else {
        console.log("Login failed. Invalid credentials.");
        // Handle login failure, e.g., show an error message
      }
    } else {
      console.log("No signup data found. Please sign up first.");
      // Handle the case where no signup data is found
    }
  };

  return (
    <div className="full-wrapper">
      <div className="header-form">
        <h3 className="descripcion-barra">
          Bienvenid@ a Interactive Light Mosaico
        </h3>
      </div>
      <form className="login" onSubmit={handleLoginSubmit}>
        <div className="input-group">
          <label htmlFor="email">
            Email
            <input
              id="email"
              type="email"
              placeholder="ejemplo@gmail.com"
              className="edit-text input-login"
              onChange={handleLoginChange}
            />
          </label>
          <label htmlFor="pass">
            Contraseña
            <input
              id="pass"
              type="password"
              placeholder="************"
              className="edit-text input-login"
              onChange={handleLoginChange}
            />
          </label>
          {/* <!-- <label for="remember" class="remember"><input id="remember" type="checkbox">Recuerdame</label> --> */}
        </div>
        <button type="submit">Ingresar</button>
      </form>
      <p className="under-form">
        No tienes cuenta?{" "}
        <a href="registro" className="links-bottom">
          Regístrate
        </a>
      </p>
    </div>
  );
};
