import React, { useState, useEffect } from "react";
import { ReactTitle } from "react-meta-tags";
import { Navbar } from "../../componentes/Navbar";
import { Header } from "../../componentes/Header";
import { Footer } from "../../componentes/Footer";
import { Barra } from "../../componentes/BarraMitad";
import "bootstrap/dist/css/bootstrap.css";
import { library } from '@fortawesome/fontawesome-svg-core';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrash, faEdit } from '@fortawesome/free-solid-svg-icons';
library.add(faTrash, faEdit);

export const UserMPH = () => {
    const [userName, setUserName] = useState("");
    const [userList, setUserList] = useState([]);

    useEffect(() => {
        const savedUserData = localStorage.getItem("userData");

        if (savedUserData) {
            const userDataObject = JSON.parse(savedUserData);
            const { nombre } = userDataObject;

            setUserName(nombre);
        }

        // Load user list from localStorage
        const savedUserList = localStorage.getItem("userList");
        if (savedUserList) {
            setUserList(JSON.parse(savedUserList));
        }
    }, []);

    // Save user list to localStorage when it changes
    useEffect(() => {
        localStorage.setItem("userList", JSON.stringify(userList));
    }, [userList]);

    const handleDeleteUser = (index) => {
        const updatedUserList = [...userList];
        updatedUserList.splice(index, 1);
        setUserList(updatedUserList);
    };

    return (
        <div className="loginpage">
            <ReactTitle title="Gestión de usuarios" />
            <Navbar />
            <Header />
            <Barra titulo={`Bienvenid@ ${userName || "admin"},`} />
            <div className="full-wrap">

                <div className="admin-wrapper">
                    <div className="button-section">
                        <div className="button-obj top">
                            <p>Gestión de usuarios</p>
                        </div>
                        <a href="/dashboard">
                            <div className="button-obj">
                                <p>Dashboard</p>
                            </div>
                        </a>
                        <a href="/registro-eventos">
                            <div className="button-obj">
                                <p>Registro de eventos</p>
                            </div>
                        </a>
                        <a href="/gestion-espacios">
                            <div className="button-obj">
                                <p>Gestión de espacios</p>
                            </div>
                        </a>
                        <a href="#">
                            <div className="button-obj">
                                <p>Ajustes</p>
                            </div>
                        </a>
                    </div>
                </div>

                <div className="section-wrapper">
                    {userList.map((user, index) => (
                        <div key={index} className="obj-wrapper">
                            <div className="user-obj">
                                <img src={user.image} alt="" />
                                <div className="info">
                                    <span id="nombre">{user.name}</span>
                                    <span id="puesto">{user.position}</span>
                                    <span id="cel">{user.phone}</span>
                                    <span id="rol">{user.role}</span>
                                    <span id="email">{user.email}</span>
                                </div>
                            </div>
                            <div className="user-obj-footer">
                                <button
                                    className="borrar"
                                    style={{ fontSize: '24px', color: 'red' }}
                                    onClick={() => handleDeleteUser(index)}
                                >
                                    <FontAwesomeIcon icon={faTrash} />
                                </button>
                                <a href="/usuarios-editar">
                                    <button className="editar" style={{ fontSize: '24px', color: 'green' }}>
                                        <FontAwesomeIcon icon={faEdit} />
                                    </button>
                                </a>
                            </div>
                        </div>
                    ))}
                </div>

            </div>
            <Footer />
        </div>
    );
};
