import { useState } from "react";
import { library } from '@fortawesome/fontawesome-svg-core';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrash, faPlus } from '@fortawesome/free-solid-svg-icons';
// handleDeleteUser
library.add(faTrash, faPlus);

export const AddUser = ({ handleAddUser }) => {
    const [userName, setUserName] = useState('');
    const [userRole, setUserRole] = useState('');
    const [userPhone, setUserPhone] = useState('');
    const [userType, setUserType] = useState('');
    const [userEmail, setUserEmail] = useState('');
    const [userImg, setUserImg] = useState('');


    const handleNameChange = (event) => {
        setUserName(event.target.value);
    };

    const handleChangeRole = (event) => {
        setUserRole(event.target.value);
    };

    const handleChangePhone = (event) => {
        setUserPhone(event.target.value);
    };

    const handleChangeType = (event) => {
        setUserType(event.target.value);
    };

    const handleChangeEmail = (event) => {
        setUserEmail(event.target.value);
    };

    const handleChangeImg = (event) => {
        setUserImg(event.target.value);
    };


    const handleSaveClick = () => {
        if (userName.trim().length > 0 && userEmail.trim().length > 0) {
            handleAddUser(userName, userRole, userPhone, userType, userEmail, userImg);
            setUserName('');
            setUserRole('');
            setUserPhone('');
            setUserType('');
            setUserEmail('');
            setUserImg('');
        }
    }

    return (
        // <div className="Note new">
        //     <input 
        //     type="text"
        //     placeholder="Título aquí"
        //     value={noteTitle}
        //     onChange={handleChangeTitle}>
        //     </input>
        //     <textarea
        //         rows={8}
        //         cols={10}
        //         placeholder="Anota algo aquí :)"
        //         value={noteText}
        //         onChange={handleChange}>
        //     </textarea>
        //     <div className="note-footer">
        //         <small>Notitas App</small>
        //         <button className="guardar" onClick={handleSaveClick}>
        //             Guardar
        //         </button>
        //     </div>
        // </div>

        <div className="obj-wrapper">
            <div className="user-obj">
                <img
                    src={userImg}
                    alt=""
                    onChange={handleChangeImg}
                />
                <div className="info">
                    <input
                        type="text"
                        className="input-user-edit"
                        placeholder="Nombre"
                        value={userName}
                        onChange={handleNameChange}
                    />

                    <input
                        type="number"
                        className="input-user-edit"
                        placeholder="Celular"
                        value={userPhone}
                        onChange={handleChangePhone}
                    />

                    <input
                        type="email"
                        className="input-user-edit"
                        placeholder="Correo"
                        value={userEmail}
                        onChange={handleChangeEmail}
                    />

                    <input
                        type="text"
                        className="input-user-edit"
                        placeholder="Cargo"
                        value={userRole}
                        onChange={handleChangeRole}
                    />
                    <span id="titulo_info_espacios">Rol:</span>
                    {/* <div className="form-check">
                        <input
                            className="form-check-input"
                            type="radio"
                            name="flexRadioDefault"
                            id="flexRadioDefault1"
                            checked={userType === 'admin'}
                            onChange={handleChangeType}
                        />
                        <label className="form-check-label" htmlFor="flexRadioDefault1">
                            Administrador
                        </label>
                    </div>
                    <div className="form-check">
                        <input
                            className="form-check-input"
                            type="radio"
                            name="flexRadioDefault"
                            id="flexRadioDefault2"
                            checked={userType === 'employee'}
                        />
                        <label className="form-check-label" htmlFor="flexRadioDefault2">
                            Empleado
                        </label>
                    </div>
                    <div className="form-check">
                        <input
                            className="form-check-input"
                            type="radio"
                            name="flexRadioDefault"
                            id="flexRadioDefault3"
                            checked={userType === 'client'}
                            defaultChecked
                        />
                        <label className="form-check-label" htmlFor="flexRadioDefault3">
                            Cliente
                        </label>
                    </div> */}
                </div>
            </div>

            <div className="user-obj-footer">
                <button className="borrar" style={{ fontSize: '24px', color: 'green' }}><FontAwesomeIcon icon={faPlus} /></button>
                {/* <a href={`/usuarios-editar`}>
                    <button className="editar" style={{ fontSize: '24px', color: 'green' }}><FontAwesomeIcon icon={faEdit} /></button>
                </a> */}
            </div>
        </div>
    )
}
